package com.fis.business.entity;import lombok.Data;import javax.persistence.*;import java.util.Date;@Data@Entity@Table(name = "PARAMETERS")public class AppParam {    private static final long serialVersionUID = 1L;    @Id    @Column(name = "APPPARAMID")    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "APP_PARAM_SEQ")    @SequenceGenerator(sequenceName = "APP_PARAM_SEQ", name = "APP_PARAM_SEQ", allocationSize = 1)    private Integer appParamId;    @Column(name = "CODE")    private String code;    @Column(name = "TYPE")    private String type;    @Column(name = "VALUE")    private String value;    @Column(name = "STATUS")    private String status;    @Column(name = "DESCRIPTION")    private String description;    @Column(name = "CREATETIME")    private Date createTime;    @Column(name = "UPDATETIME")    private Date updateTime;}